mapper.update:
update tb_blog
set blog_status = #{status} where blog_id = #{blogId}
and is_deleted = 0

mapper.select:
select
,
from tb_blog where blog_status = 1 and is_deleted = 0 order by create_time desc limit #{start} , #{limit}

mapper.update:
update tb_blog
set is_deleted = 1
where blog_id = #{blogId,jdbcType=INTEGER}
